// Função que simula respostas do bot
async function simularRespostaBot(pergunta) {
    try {
        console.log("Enviando pergunta:", pergunta);
        const resposta = await fetch("/api/chat", {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({
                message: pergunta
            })
        });
        if (!resposta.ok) {
            throw new Error("Erro ao obter resposta do servidor");
        }
        const data = await resposta.json();
        console.log("Resposta recebida:", data);
        if (!data.resposta) {
            throw new Error("Resposta inválida do servidor");
        }
        return data.resposta;
    } catch (erro) {
        console.error("Erro ao processar pergunta:", erro);
        return "Desculpe, ocorreu um erro ao processar sua pergunta. Por favor, tente novamente.";
    }
}
